option.value:
@echo off
echo ========================================
echo   LIMPIEZA DE ARCHIVOS MARKDOWN ANTIGUOS
echo ========================================
echo.
echo Este script eliminara los archivos Markdown antiguos de la raiz
echo que ya han sido movidos y reorganizados en la carpeta docs/
echo.

pause

echo.
echo Eliminando archivos antiguos...
echo.

if exist VERIFICACION_TEMA_TIPOGRAFIA.md (
    del VERIFICACION_TEMA_TIPOGRAFIA.md
    echo [OK] VERIFICACION_TEMA_TIPOGRAFIA.md eliminado
) else (
    echo [--] VERIFICACION_TEMA_TIPOGRAFIA.md no existe
)

if exist MODULARIZACION_COMPLETADA.md (
    del MODULARIZACION_COMPLETADA.md
    echo [OK] MODULARIZACION_COMPLETADA.md eliminado
) else (
    echo [--] MODULARIZACION_COMPLETADA.md no existe
)

if exist RESUMEN_COMPONENTES.md (
    del RESUMEN_COMPONENTES.md
    echo [OK] RESUMEN_COMPONENTES.md eliminado
) else (
    echo [--] RESUMEN_COMPONENTES.md no existe
)

if exist CHECKLIST.md (
    del CHECKLIST.md
    echo [OK] CHECKLIST.md eliminado
) else (
    echo [--] CHECKLIST.md no existe
)

if exist PASOS_SIGUIENTES.md (
    del PASOS_SIGUIENTES.md
    echo [OK] PASOS_SIGUIENTES.md eliminado
) else (
    echo [--] PASOS_SIGUIENTES.md no existe
)

if exist RESUMEN_REORGANIZACION.md (
    del RESUMEN_REORGANIZACION.md
    echo [OK] RESUMEN_REORGANIZACION.md eliminado
) else (
    echo [--] RESUMEN_REORGANIZACION.md no existe
)

echo.
echo ========================================
echo   LIMPIEZA COMPLETADA
echo ========================================
echo.
echo Los siguientes archivos se mantienen:
echo - README.md (principal del proyecto)
echo - RESUMEN_FINAL_COMPLETO.md (resumen de todo el trabajo)
echo - docs/ (carpeta con documentacion organizada)
echo.

pause
